import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Link } from 'react-router-dom';


const Login = ({auth, tokensetter}) => {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });

    const { email, password } = formData;

    const onChange = e => setFormData( { ...formData, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();
        //check whether user is auth and return token if authenticated
        fetch('http://localhost:8000/auth/users/', {
            method: "POST"
        }).then


        //login(email, password)
    }

    //check whether user authenticated
    //redirect to the homepage

    return ( 
        <div className="login">
            <div>
                <Form>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control value={email} onChange={e => onChange(e)} required type="email" placeholder="Enter email" />
                        <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
                        </Form.Text>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control value={password} onChange={e => onChange(e)} required type="password" placeholder="Password" />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Log in
                    </Button>
                </Form>
            </div>

            <br />
            <Link className='signuplink' to="/signup">No Account? Sign up here!</Link>
        </div>
    );
}

export default Login;
